import React, { useState } from 'react';
import jsPDF from 'jspdf';
import './InvoiceGenerator.css';

const InvoiceGenerator = () => {
  const [gstin, setGstin] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [date, setDate] = useState('');
  const [to, setTo] = useState('');
  const [partyGstin, setPartyGstin] = useState('');
  const [clientAddress, setClientAddress] = useState('');

  const [items, setItems] = useState([
    { description: 'Drilling Charges', depth: '001 TO 100', quantity: '', price: '', total: 0 },
    { description: 'Drilling Charges', depth: '101 TO 200', quantity: '', price: '', total: 0 },
    { description: 'Casing Pipe PVC 7"', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Casing Pipe PVC 10"', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Coller', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Wielding', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Labor & Transport', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Water injection', depth: '', quantity: '', price: '', total: 0 },
    { description: 'Filter casing', depth: '', quantity: '', price: '', total: 0 },
  ]);

  const calculateTotals = () => {
    let netTotal = items.reduce((sum, item) => sum + parseFloat(item.total || 0), 0);
    let gst = (netTotal * 0.18).toFixed(2);
    let grossTotal = (netTotal + parseFloat(gst)).toFixed(2);
    return { netTotal: netTotal.toFixed(2), gst, grossTotal };
  };

  const handleInputChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;

    if (field === 'quantity' || field === 'price') {
      const quantity = parseFloat(newItems[index].quantity) || 0;
      const price = parseFloat(newItems[index].price) || 0;
      newItems[index].total = (quantity * price).toFixed(2);
    }

    setItems(newItems);
  };

  const generatePDF = () => {
    const { netTotal, gst, grossTotal } = calculateTotals();
    const doc = new jsPDF();

    doc.text("SRI VINAYAKA BOREWELLS", 10, 10);
    doc.text("#1289, 2nd Cross, 6th Main, Kariyanapalya, Uttarahalli, Bangalore 98", 10, 20);
    doc.text("Invoice", 10, 30);

    doc.text(`GSTIN: ${gstin}`, 10, 40);
    doc.text(`Mobile No: ${mobileNo}`, 10, 50);
    doc.text(`To: ${to}`, 10, 60);
    doc.text(`Client GSTIN NO: ${partyGstin}`, 10, 70);
    doc.text(`Address Of Client: ${clientAddress}`, 10, 80);
    doc.text(`Place of Supply & Work: Kormangla, Bangalore`, 10, 90);
    doc.text(`Date: ${date}`, 10, 100);

    doc.text('Invoice Details:', 10, 110);
    let y = 120;
    items.forEach((item, index) => {
      doc.text(
        `${index + 1}. ${item.description} ${item.depth ? `- Depth: ${item.depth}` : ''} - Qty: ${item.quantity} - Price: ${item.price} - Amount: ${item.total}`,
        10,
        y
      );
      y += 10;
    });

    doc.text(`Net Total: ${netTotal}`, 10, y);
    doc.text(`Add GST 18%: ${gst}`, 10, y + 10);
    doc.text(`Gross Amount: ${grossTotal}`, 10, y + 20);
    doc.text(`Authorized Signature`, 10, y + 40);

    doc.save('invoice.pdf');
  };

  const { netTotal, gst, grossTotal } = calculateTotals();

  return (
    <div className="invoice-generator">
      <h1>Invoice Generator</h1>
      <label>GSTIN:</label>
      <input type="text" value={gstin} onChange={(e) => setGstin(e.target.value)} />
      <label>Mobile No:</label>
      <input type="text" value={mobileNo} onChange={(e) => setMobileNo(e.target.value)} />
      <label>Date:</label>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <label>To:</label>
      <input type="text" value={to} onChange={(e) => setTo(e.target.value)} />
      <label>Client GSTIN NO:</label>
      <input type="text" value={partyGstin} onChange={(e) => setPartyGstin(e.target.value)} />
      <label>Client Address:</label>
      <input type="text" value={clientAddress} onChange={(e) => setClientAddress(e.target.value)} />

      <table>
        <thead>
          <tr>
            <th>SL No</th>
            <th>Description</th>
            <th>Depth</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Total Amount</th> {/* New Column */}
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.description}</td>
              <td>{item.depth}</td>
              <td>
                <input
                  type="number"
                  value={item.quantity}
                  onChange={(e) => handleInputChange(index, 'quantity', e.target.value)}
                  placeholder="Quantity"
                />
              </td>
              <td>
                <input
                  type="number"
                  value={item.price}
                  onChange={(e) => handleInputChange(index, 'price', e.target.value)}
                  placeholder="Price"
                />
              </td>
              <td>{item.total}</td> {/* Displays Total Amount */}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Totals Section */}
      <div className="totals">
        <p><strong>Net Total:</strong> ₹{netTotal}</p>
        <p><strong>Add GST 18%:</strong> ₹{gst}</p>
        <p><strong>Gross Amount:</strong> ₹{grossTotal}</p>
      </div>

      <button onClick={generatePDF}>Generate PDF</button>
    </div>
  );
};

export default InvoiceGenerator;
