import React, { useState } from "react";
import "./InvoiceGenerator.css";

const InvoiceGenerator = () => {
  const [rows, setRows] = useState([
    { description: "", depth: "", quantity: "", price: "", amount: 0 },
  ]);
  const [netTotal, setNetTotal] = useState(0);

  const handleInputChange = (index, event) => {
    const { name, value } = event.target;
    const updatedRows = [...rows];
    updatedRows[index][name] = value;

    // Calculate amount (Quantity * Price)
    if (name === "quantity" || name === "price") {
      updatedRows[index].amount =
        (updatedRows[index].quantity || 0) * (updatedRows[index].price || 0);
    }

    setRows(updatedRows);
    calculateTotal(updatedRows);
  };

  const calculateTotal = (rows) => {
    const total = rows.reduce((sum, row) => sum + (row.amount || 0), 0);
    setNetTotal(total);
  };

  const addRow = () => {
    setRows([...rows, { description: "", depth: "", quantity: "", price: "", amount: 0 }]);
  };

  return (
    <div className="invoice-container">
      <header className="invoice-header">
        <h1>SRI VINAYAKA BOREWELLS</h1>
        <p>#1289, 2nd Cross, 6th Main, Kariyanapalya, Uttarahalli, Subramanyapura post, 6th Stage, Bangalore 98</p>
        <a href="#">Invoice</a>
      </header>

      <div className="invoice-details">
        <div className="detail-row">
          <label>GSTIN:</label>
          <input type="text" />
          <label>Mobile No:</label>
          <input type="text" defaultValue="9742888824" />
        </div>
        <div className="detail-row">
          <label>To:</label>
          <input type="text" />
          <label>Place of Supply & Work:</label>
          <input type="text" defaultValue="Kormangla, Bangalore" />
        </div>
        <div className="detail-row">
          <label>Address Of Client:</label>
          <input type="text" />
          <label>Date:</label>
          <input type="date" />
        </div>
      </div>

      <table className="invoice-table">
        <thead>
          <tr>
            <th>Description</th>
            <th>Depth</th>
            <th>Quantity</th>
            <th>Rate</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => (
            <tr key={index}>
              <td><input type="text" name="description" value={row.description} onChange={(e) => handleInputChange(index, e)} /></td>
              <td><input type="text" name="depth" value={row.depth} onChange={(e) => handleInputChange(index, e)} /></td>
              <td><input type="number" name="quantity" value={row.quantity} onChange={(e) => handleInputChange(index, e)} /></td>
              <td><input type="number" name="price" value={row.price} onChange={(e) => handleInputChange(index, e)} /></td>
              <td>{row.amount.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={addRow}>Add Row</button>

      <div className="invoice-summary">
        <p>Net Total: ₹{netTotal.toFixed(2)}</p>
        <p>Add CGST 9%: ₹{(netTotal * 0.09).toFixed(2)}</p>
        <p>Add SGST 9%: ₹{(netTotal * 0.09).toFixed(2)}</p>
        <p>Gross Total: ₹{(netTotal * 1.18).toFixed(2)}</p>
      </div>

      <div className="invoice-footer">
        <p>Bank: SBI Bank</p>
        <p>Account Name: Sri Vinayaka Borewells</p>
        <p>Account Number: 31691791005</p>
        <p>IFSC: SBIN0014962</p>
        <p>Authorized Signature:</p>
      </div>
    </div>
  );
};

export default InvoiceGenerator;
