import React, { useState } from 'react';
import jsPDF from 'jspdf';
import './InvoiceGenerator.css'; // Import the CSS file

const InvoiceGenerator = () => {
  const [gstin, setGstin] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [date, setDate] = useState('');
  const [items, setItems] = useState([
    { description: 'Drilling Charges', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 7"', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 10"', quantity: '', price: '' },
    { description: 'Coller', quantity: '', price: '' },
    { description: 'Wielding', quantity: '', price: '' },
    { description: 'Labor & Transport', quantity: '', price: '' },
    { description: 'Water injection', quantity: '', price: '' },
    { description: 'Filter casing', quantity: '', price: '' },
  ]);

  const calculateTotals = () => {
    let netTotal = 0;
    items.forEach(item => {
      const amount = item.quantity * item.price || 0;
      netTotal += amount;
    });
    const cgst = (netTotal * 0.09).toFixed(2);
    const sgst = (netTotal * 0.09).toFixed(2);
    const grossTotal = (netTotal + parseFloat(cgst) + parseFloat(sgst)).toFixed(2);
    return { netTotal, cgst, sgst, grossTotal };
  };

  const handleInputChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    setItems(newItems);
  };

  const generatePDF = () => {
    const { netTotal, cgst, sgst, grossTotal } = calculateTotals();
    const doc = new jsPDF();

    doc.text(`GSTIN: ${gstin}`, 10, 10);
    doc.text(`Mobile No: ${mobileNo}`, 10, 20);
    doc.text(`Date: ${date}`, 10, 30);
    doc.text('Invoice Details:', 10, 40);

    items.forEach((item, index) => {
      doc.text(`${index + 1}. ${item.description} - Qty: ${item.quantity} - Price: ${item.price} - Amount: ${(item.quantity * item.price).toFixed(2)}`, 10, 50 + (index * 10));
    });

    doc.text(`NET TOTAL: ${netTotal.toFixed(2)}`, 10, 50 + (items.length * 10));
    doc.text(`ADD CGST 9%: ${cgst}`, 10, 60 + (items.length * 10));
    doc.text(`ADD SGST 9%: ${sgst}`, 10, 70 + (items.length * 10));
    doc.text(`GROSS TOTAL: ${grossTotal}`, 10, 80 + (items.length * 10));

    doc.save('invoice.pdf');
  };

  return (
    <div className="invoice-generator">
      <h1>Invoice Generator</h1>
      <div>
        <label>GSTIN:</label>
        <input type="text" value={gstin} onChange={(e) => setGstin(e.target.value)} />
      </div>
      <div>
        <label>Mobile No:</label>
        <input type="text" value={mobileNo} onChange={(e) => setMobileNo(e.target.value)} />
      </div>
      <div>
        <label>Date:</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      </div>
      <table>
        <thead>
          <tr>
            <th>SL No</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.description}</td>
              <td>
              <input
                type="number"
                value={item.quantity}
                onChange={(e) => handleInputChange(index, 'quantity', e.target.value)}
                placeholder="Quantity"
              />
              </td>
              <td>
                <input
                  type="number"
                  value={item.price}
                  onChange={(e) => handleInputChange(index, 'price', e.target.value)}
                  placeholder="Price"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={generatePDF}>Generate PDF</button>
    </div>
  );
};

export default InvoiceGenerator;
