import React, { useState } from 'react';
import jsPDF from 'jspdf';
import './InvoiceGenerator.css';

const InvoiceGenerator = () => {
  const [gstin, setGstin] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [date, setDate] = useState('');
//   const [to, setTo] = useState('ABC Company');
//   const [partyGstin, setPartyGstin] = useState('');
  const [clientAddress, setClientAddress] = useState('White Field');
  const [items, setItems] = useState([
    { description: 'Drilling Charges', depth: '', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 7"', depth: '', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 10"', depth: '', quantity: '', price: '' },
    { description: 'Coller', depth: '', quantity: '', price: '' },
    { description: 'Wielding', depth: '', quantity: '', price: '' },
    { description: 'Labor & Transport', depth: '', quantity: '', price: '' },
    { description: 'Water injection', depth: '', quantity: '', price: '' },
    { description: 'Filter casing', depth: '', quantity: '', price: '' },
  ]);

  const calculateTotals = () => {
    let netTotal = 0;
    items.forEach(item => {
      const amount = (item.quantity * item.price) || 0;
      netTotal += amount;
    });
    const cgst = (netTotal * 0.09).toFixed(2);
    const sgst = (netTotal * 0.09).toFixed(2);
    const grossTotal = (netTotal + parseFloat(cgst) + parseFloat(sgst)).toFixed(2);
    return { netTotal, cgst, sgst, grossTotal };
  };

  const handleInputChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    setItems(newItems);
  };

  const generatePDF = () => {
    const { netTotal, cgst, sgst, grossTotal } = calculateTotals();
    const doc = new jsPDF();

    doc.text("SRI VINAYAKA BOREWELLS", 10, 10);
    doc.text("#1289, 2nd Cross, 6th Main, Kariyanapalya, Uttarahalli, Subramanyapura post, 6th Stage, Bangalore 98", 10, 20);
    doc.text("Invoice", 10, 30);
    doc.text(`GSTIN: ${gstin}    Mobile No: ${mobileNo}`, 10, 40);
    doc.text(`To: ${to}    Place of Supply & Work: Kormangla, Bangalore`, 10, 50);
    doc.text(`Address Of Client: ${clientAddress}`, 10, 60);
    doc.text(`Date: ${date}`, 10, 70);

    doc.text('Invoice Details:', 10, 80);
    items.forEach((item, index) => {
      doc.text(`${index + 1}. ${item.description} - Depth: ${item.depth} - Qty: ${item.quantity} - Price: ${item.price} - Amount: ${(item.quantity * item.price).toFixed(2)}`, 10, 90 + (index * 10));
    });

    doc.text(`NET TOTAL: ${netTotal.toFixed(2)}`, 10, 100 + (items.length * 10));
    doc.text(`ADD CGST 9%: ${cgst}`, 10, 110 + (items.length * 10));
    doc.text(`ADD SGST 9%: ${sgst}`, 10, 120 + (items.length * 10));
    doc.text(`GROSS TOTAL: ${grossTotal}`, 10, 130 + (items.length * 10));

    doc.text("Bank: SBI Bank", 10, 140 + (items.length * 10));
    doc.text("Account Name: Sri Vinayaka Borewells", 10, 150 + (items.length * 10));
    doc.text("ACC Number: 31691791005    IFSC: SBIN0014962", 10, 160 + (items.length * 10));
    doc.text("Authorized Signature", 10, 170 + (items.length * 10));

    doc.save('invoice.pdf');
  };

  return (
    <div className="invoice-generator">
      <h1>Invoice Generator</h1>
      <div>
        <label>GSTIN:</label>
        <input type="text" value={gstin} onChange={(e) => setGstin(e.target.value)} />
      </div>
      <div>
        <label>Mobile No:</label>
        <input type="text" value={mobileNo} onChange={(e) => setMobileNo(e.target.value)} />
      </div>
      <div>
        <label>Client Address:</label>
        <input type="text" value={clientAddress} onChange={(e) => setClientAddress(e.target.value)} />
      </div>
      <div>
        <label>Date:</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      </div>
      <table>
        <thead>
          <tr>
            <th>SL No</th>
            <th>Description</th>
            <th>Depth</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.description}</td>
              <td><input type="text" value={item.depth} onChange={(e) => handleInputChange(index, 'depth', e.target.value)} /></td>
              <td><input type="number" value={item.quantity} onChange={(e) => handleInputChange(index, 'quantity', e.target.value)} /></td>
              <td><input type="number" value={item.price} onChange={(e) => handleInputChange(index, 'price', e.target.value)} /></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={generatePDF}>Generate PDF</button>
    </div>
  );
};

export default InvoiceGenerator;
