import React, { useState } from 'react';
import jsPDF from 'jspdf';
import './InvoiceGenerator.css'; // Import the CSS file

const InvoiceGenerator = () => {
  const [gstin, setGstin] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [date, setDate] = useState('');
  const [to, setTo] = useState('MOJO CAMPUS');
  const [partyGstin, setPartyGstin] = useState('');
  const [items, setItems] = useState([
    { description: 'Drilling Charges', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 7"', quantity: '', price: '' },
    { description: 'Casing Pipe PVC 10"', quantity: '', price: '' },
    { description: 'Coller', quantity: '', price: '' },
    { description: 'Wielding', quantity: '', price: '' },
    { description: 'Labor & Transport', quantity: '', price: '' },
    { description: 'Water injection', quantity: '', price: '' },
    { description: 'Filter casing', quantity: '', price: '' },
  ]);

  const calculateTotals = () => {
    let netTotal = 0;
    items.forEach(item => {
      const amount = item.quantity * item.price || 0;
      netTotal += amount;
    });
    const cgst = (netTotal * 0.09).toFixed(2);
    const sgst = (netTotal * 0.09).toFixed(2);
    const grossTotal = (netTotal + parseFloat(cgst) + parseFloat(sgst)).toFixed(2);
    return { netTotal, cgst, sgst, grossTotal };
  };

  const handleInputChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    setItems(newItems);
  };

  const generatePDF = () => {
    const { netTotal, cgst, sgst, grossTotal } = calculateTotals();
    const doc = new jsPDF();

    // Add Company Information
    doc.text("SRI VINAYAKA BOREWELLS", 10, 10);
    doc.text("#1289, 2nd Cross, 6th Main, Kariyanapalya, Uttarahalli, Subramanyapura post, 6th Stage, Bangalore 98", 10, 20);
    doc.text("Invoice", 10, 30);

    // Add GSTIN and Mobile No
    doc.text(`GSTIN: ${gstin}`, 10, 40);
    doc.text(`Mobile No: ${mobileNo}`, 10, 50);

    // Add To and Place of Supply
    doc.text(`To: ${to}`, 10, 60);
    doc.text(`Party GSTIN NO: ${partyGstin}`, 10, 70);
    doc.text(`Place of Supply & Work: Kormangla, Bangalore`, 10, 80);
    doc.text(`Date: ${date}`, 10, 90);

    // Add Invoice Details
    doc.text('Invoice Details:', 10, 100);
    items.forEach((item, index) => {
      doc.text(`${index + 1}. ${item.description} - Qty: ${item.quantity} - Price: ${item.price} - Amount: ${(item.quantity * item.price).toFixed(2)}`, 10, 110 + (index * 10));
    });

    // Add Totals
    doc.text(`NET TOTAL: ${netTotal.toFixed(2)}`, 10, 110 + (items.length * 10));
    doc.text(`ADD CGST 9%: ${cgst}`, 10, 120 + (items.length * 10));
    doc.text(`ADD SGST 9%: ${sgst}`, 10, 130 + (items.length * 10));
    doc.text(`GROSS TOTAL: ${grossTotal}`, 10, 140 + (items.length * 10));

    doc.save('invoice.pdf');
  };

  return (
    <div className="invoice-generator">
      <h1>Invoice Generator</h1>
      <div>
        <label>GSTIN:</label>
        <input type="text" value={gstin} onChange={(e) => setGstin(e.target.value)} />
      </div>
      <div>
        <label>Mobile No:</label>
        <input type="text" value={mobileNo}
                onChange={(e) => setMobileNo(e.target.value)} />
                </div>
                <div>
                  <label>Date:</label>
                  <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                </div>
                <div>
                  <label>To:</label>
                  <input type="text" value={to} onChange={(e) => setTo(e.target.value)} />
                </div>
                <div>
                  <label>Party GSTIN NO:</label>
                  <input type="text" value={partyGstin} onChange={(e) => setPartyGstin(e.target.value)} />
                </div>
                <table>
                  <thead>
                    <tr>
                      <th>SL No</th>
                      <th>Description</th>
                      <th>Quantity</th>
                      <th>Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map((item, index) => (
                      <tr key={index}>
                        <td>{index + 1}</td>
                        <td>{item.description}</td>
                        <td>
                          <input
                            type="number"
                            value={item.quantity}
                            onChange={(e) => handleInputChange(index, 'quantity', e.target.value)}
                            placeholder="Quantity"
                          />
                        </td>
                        <td>
                          <input
                            type="number"
                            value={item.price}
                            onChange={(e) => handleInputChange(index, 'price', e.target.value)}
                            placeholder="Price"
                          />
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <button onClick={generatePDF}>Generate PDF</button>
              </div>
            );
          };

          export default InvoiceGenerator;
